mapper.select:
SELECT p.parking_id, p.number, p.owner, u.name
FROM parking p
         LEFT JOIN wx_user u ON p.owner = u.id
WHERE p.parking_id = #{parkingId}
ORDER BY p.parking_id;

SELECT p.parking_id, p.number, p.owner, u.name
FROM parking p
         LEFT JOIN wx_user u ON p.owner = u.id
WHERE p.number = #{number}
ORDER BY p.parking_id;
